import React, { Component } from 'react'

class Octave {
  constructor(a){
    this.b = {
      freq: a * Math.pow(2,  2/12),
      noteName: 'b'
    }
    this.aSharp = {
      freq: a * Math.pow(2,  1/12),
      noteName: 'a#'
    }
    this.a = {
      freq: a * Math.pow(2,  0/12),
      noteName: 'a'
    }
    this.gSharp = {
      freq: a * Math.pow(2, -1/12),
      noteName: 'g#'
    } 
    this.g = {
      freq: a * Math.pow(2, -2/12),
      noteName: 'g'
    } 
    this.fSharp = {
      freq: a * Math.pow(2, -3/12),
      noteName: 'f#'
    } 
    this.f = {
      freq: a * Math.pow(2, -4/12),
      noteName: 'f'
    } 
    this.e = {
      freq: a * Math.pow(2, -5/12),
      noteName: 'e'
    } 
    this.dSharp = {
      freq: a * Math.pow(2, -6/12),
      noteName: 'd#'
    } 
    this.d = {
      freq: a * Math.pow(2, -7/12),
      noteName: 'd'
    } 
    this.cSharp = {
      freq: a * Math.pow(2, -8/12),
      noteName: 'c#'
    }  
    this.c     = {
      freq: a * Math.pow(2, -9/12),
      noteName: 'c'
    } 
  }
}

const baseFrequency = 440
const octaves = 4
const notes = []

for (var i = 0 - octaves/2; i < octaves/2; i++) {
  let a = baseFrequency * Math.pow(2, i)
  let octave = new Octave(a)
  notes.push(octave)
}

class Key extends Component {
  render() {
    const { note } = this.props
    return <div>{note}</div>
  }
}

class Keyboard extends Component {
  renderOctave = octave => {
    const keys = Object.keys(octave)
    console.log(keys)
    // const 
    return Object.values(octave).map(note => <Key key={note} note={note} />)
  }
  render() {
    console.log(notes)
    return <div>
      {/* {notes.map(octave => this.renderOctave(octave))} */}
    </div>
  }
}

export default Keyboard
